function arrToObj(arr){
    let obj = {
        address: arr[0],
    }
    obj.ADS1115 = {}
    for(let key in arr){
        for(let i = 1; i < 9; i++ ){
            if(i == key){
                if(arr[key] == "nc"){
                    obj.ADS1115[key] = "ADS is not connected"
                }else{
                    obj.ADS1115[key] = arr[key]
                }
                break
            }
        }
    }
    return obj
}
module.exports = arrToObj